mapper.select:
select date_format(workDate,'%d') as workDate, date_format(startTime,'%H:%i:%s') as startTime, date_format(endTime,'%H:%i:%s') as endTime, date_format(restTime,'%H:%i') as restTime, date_format(workTime,'%H:%i') as workTime
from workapp_info_table where email=#{param1} and workDate like CONCAT(#{param2}, '%')
order by workDate asc

mapper.update:
update workapp_info_table
set startTime= #{startTime},endTime= #{endTime},restTime=#{restTime}
where email = #{email} and workDate like CONCAT(#{workDate}, '%')

update 
	app_account_table 
set 
	password = #{0}
where 
	email = #{1}
